# ----------------- CONTENT RENDERERS -----------------

def render_company_card_html(name, info, logo_url, updates, is_saved):
    
    # Process Updates - SHOWING FULL TEXT NOW
    update_html = ""
    if updates:
        latest = updates[0]
        # Removed the [:25] slice to show complete message
        update_html = f'<div class="live-badge"> {latest["type"]}: {latest["message"]}</div>'
    
    # Process Roles for Display
    roles_display = roles_to_str(info.get('roles', []))[:35] + "..." if len(roles_to_str(info.get('roles', []))) > 35 else roles_to_str(info.get('roles', []))

    html = f"""
<div class="company-card">
<div class="watermark-bg" style="background-image: url('{logo_url}');"></div>

<div class="card-content-wrapper">
<div style="width: 100%; display: flex; flex-direction: column; align-items: center;">
{update_html}
<div class="card-title">{name}</div>
<div class="card-role-label">{roles_display}</div>
<div class="card-overview">
{info.get('overview', '')}
</div>
</div>

<div style="width: 100%; text-align: center; margin-top: auto;">
<div style="font-size: 0.8rem; color: #64748B; margin-bottom: 5px;">
{len(info.get('selection_stages', []))} Selection Rounds
</div>
</div>
</div>
</div>
"""
    return html

