# ----------------- MAIN PAGE FUNCTION -----------------
def show_hiring_aptitude_page():
    
    initialize_hiring_session_state()
    inject_custom_css()
    
    # ----------------- HEADER -----------------
    col_logo, col_home = st.columns([4, 1])
    with col_logo:
        st.markdown('<div style="color: #00B4D8; font-weight: 800; font-size: 1.5rem; letter-spacing: -1px;">BCAsprint</div>', unsafe_allow_html=True)
    with col_home:
        if st.button("Home", key="nav_home_top"):
            st.session_state.current_page = "Home"
            st.rerun()

    # Hero Banner
    if not (st.session_state["show_trends"] or st.session_state["show_tips"]):
        st.markdown("""
<div class="hero-header">
<h1 class="hero-title">OPPORTUNITY RADAR</h1>
<p class="hero-subtitle">
    Real-time tracking of BCA hiring drives, eligibility criteria, and syllabus.
</p>
</div>
""", unsafe_allow_html=True)

    # ----------------- CONTROLS -----------------
    
    # Navigation Tabs
    nav1, nav2, nav3, nav4 = st.columns(4)
    with nav1:
        if st.button("BROWSE JOBS"):
            st.session_state["show_saved"] = False
            st.session_state["show_trends"] = False
            st.session_state["show_tips"] = False
            st.rerun()
    with nav2:
        if st.button("SAVED"):
            st.session_state["show_saved"] = True
            st.session_state["show_trends"] = False
            st.session_state["show_tips"] = False
            st.rerun()
    with nav3:
        if st.button("TRENDS"):
            st.session_state["show_trends"] = True
            st.session_state["show_saved"] = False
            st.session_state["show_tips"] = False
            st.rerun()
    with nav4:
        if st.button("TIPS"):
            st.session_state["show_tips"] = True
            st.session_state["show_saved"] = False
            st.session_state["show_trends"] = False
            st.rerun()

    # Search & Refresh (Only show in Browse/Saved modes)
    if not (st.session_state["show_trends"] or st.session_state["show_tips"]):
        st.markdown('<div class="spacer-20"></div>', unsafe_allow_html=True)
        c_search, c_refresh = st.columns([4, 1])
        with c_search:
            q = st.text_input("Search", value=st.session_state["hiring_search_query"], 
                              placeholder=" Search companies, roles, or keywords...", 
                              label_visibility="collapsed")
            if q != st.session_state["hiring_search_query"]:
                st.session_state["hiring_search_query"] = q
                st.rerun()
        with c_refresh:
            if st.button("Refresh Data", use_container_width=True):
                refresh_live_data()
            if st.session_state.get("refresh_success", False):
                st.success("Updated!")
                st.session_state["refresh_success"] = False

    st.markdown('<div class="spacer-40"></div>', unsafe_allow_html=True)

    # ----------------- CONTENT RENDERING -----------------

    if st.session_state["show_trends"]:
        show_hiring_trends()
        
    elif st.session_state["show_tips"]:
        show_preparation_tips()
        
    else:
        # --- BROWSE / SAVED JOBS GRID ---
        filter_saved = st.session_state["show_saved"]
        if filter_saved:
             st.markdown("###  Your Saved Companies")
             if not st.session_state["saved_companies"]:
                 st.info("No saved companies yet.")
        
        # Filter Logic
        filtered = []
        for name in COMPANY_ORDER:
            # Saved Filter
            if filter_saved and name not in st.session_state["saved_companies"]:
                continue
            
            # Search Filter
            info = COMPANY_DETAILS[name]
            search_str = f"{name} {info['overview']} {roles_to_str(info['roles'])}".lower()
            if q.lower() not in search_str:
                continue
            
            filtered.append(name)

        # Render Grid (3 Columns per row)
        if filtered:
            for i in range(0, len(filtered), 3):
                cols = st.columns(3)
                for j in range(3):
                    if i + j < len(filtered):
                        name = filtered[i + j]
                        info = COMPANY_DETAILS[name]
                        logo = ONLINE_LOGOS.get(name, FALLBACK_LOGO)
                        updates = st.session_state["company_updates"].get(name, [])
                        is_saved = name in st.session_state["saved_companies"]
                        
                        with cols[j]:
                            # 1. Visual Card HTML
                            st.markdown(render_company_card_html(name, info, logo, updates, is_saved), unsafe_allow_html=True)
                            
                            # 2. Interactive Buttons (Expander & Save)
                            # We use columns inside to organize the buttons below the card
                            c_exp, c_save = st.columns([5, 1])
                            
                            with c_exp:
                                with st.expander("DETAILS & APPLY"):
                                    # Details Content
                                    st.markdown(f"**Eligibility:** {info.get('eligibility')}")
                                    st.markdown("---")
                                    st.markdown("**Syllabus:**")
                                    for sy in info.get('syllabus', []):
                                        st.markdown(f"- {sy}")
                                    
                                    st.markdown("<br>", unsafe_allow_html=True)
                                    st.link_button(" Apply Now", info.get("apply_link", "#"), use_container_width=True)
                            
                            with c_save:
                                # Vertical spacer to align star
                                st.markdown("<div style='height: 5px'></div>", unsafe_allow_html=True)
                                btn_label = "" if is_saved else ""
                                key_prefix = "saved" if filter_saved else "browse"
                                if st.button(btn_label, key=f"save_{key_prefix}_{name}"):
                                    toggle_saved_company(name)
                                    st.rerun()
                        
                        # Spacer between rows
                        st.markdown("<div class='spacer-20'></div>", unsafe_allow_html=True)
        else:
            st.markdown("""
<div style="text-align: center; padding: 40px; color: #94A3B8;">
<h3>No companies found matching your criteria.</h3>
</div>
""", unsafe_allow_html=True)

# ----------------- EXECUTION -----------------
if __name__ == "__main__":
    show_hiring_aptitude_page()